import './App.css';
import Header from './MyComponents/Header';
import { Todos } from './MyComponents/Todos';
import { Footer } from './MyComponents/Footer';
import React, { useState, useEffect } from 'react';
import { AddTodo } from './MyComponents/AddTodo';
function App() {

  let initTodo;
  if(localStorage.setItem("todos"))
  {
    initTodo=[];
  }
  else
  {
    initTodo=JSON.parse(localStorage.getItem("todos"));
  }
  localStorage.setItem("todos",JSON.stringify(todos));  


  const onDelete = (todo) => {

    console.log("This is OnDelete of todo ", todo);
    setTodos(todos.filter((e) => {
      return e !== todo;
    }));
    localStorage.setItem("todos",JSON.stringify(todos));   
  }



  const AddTodoList = (title, description) => {
    console.log("I am add todo", title, description)
    let sno
    if(todos.length==0)
    {
      sno=1
    }
    else
    {
      sno=todos[todos.length-1].sno+1
    }



    const Todo=
    {
      sno:sno,
      title:title,
      desc:description,
    }
    setTodos([...todos,Todo]);
    console.log(Todo);
    localStorage.setItem("todos",JSON.stringify(todos));    
  }

  const[todos,setTodos]=useState(initTodo);

  useEffect(() => {
    localStorage.setItem("todos",JSON.stringify(todos));
  }, [todos])



  return (
    <>
      <Header title="Bhumi's To-do List" search={false} />
      <AddTodo AddTodoList={AddTodoList} />
      <Todos todos={todos} onDelete={onDelete} />
      <Footer />
    </>
  );
}

export default App;
